import React, { useEffect } from "react";
import stack_overflow_icon from "../stack_overflow_icon.png";
import { MdMenu } from "react-icons/md";
import { FaUserCircle } from "react-icons/fa";
import { FaSearch } from "react-icons/fa";
import { Link } from "react-router-dom";
import AskQuestion from "./AskQuestionBar";
import Leftbar from "./Leftbar";
import Navbar from "./Navbar";
import Rightbar from "./Rightbar";

export default function Home() {
  const sortingButtons = ["Bountied", "Hot", "Week", "Month"];
  const [questions, setQuestions] = React.useState<string[]>([]);

  useEffect(() => {
    var todos = Gun().get("todos");
    todos.map().on(function (todo: any, id: any) {
      console.log("todo<<<<<", ...questions);
      // setQuestions([...questions, todo["title"]]);
      setQuestions((prevState) => [...prevState, todo["title"]]);
    });
  }, []);

  {
    console.log("final>>>>>", questions);
  }
  return (
    <React.Fragment>
      <div className="h-screen w-full bg-neutral-800 sm:w-7/12">
        <div className="mx-4 flex items-center justify-between pt-4 sm:items-start">
          <p className="text-xl text-white md:text-3xl">Top Questions</p>
          <Link
            to={"/askquestion"}
            className="cursor-pointer rounded-sm bg-blue-500 py-2 px-1 text-center text-white sm:h-10"
          >
            Ask Question
          </Link>
        </div>

        <div className="relative mx-4 mt-6 flex justify-end">
          {sortingButtons.map((buttonName) => {
            return (
              <button className="border-collapse border border-gray-400 p-3 text-white transition-all duration-100 hover:bg-slate-600">
                {buttonName}
              </button>
            );
          })}
        </div>

        <div id="content">
          {questions.map((question) => {
            <p>{question}</p>
          })}
          }
        </div>
      </div>
      <Rightbar />
    </React.Fragment>
  );
}
