        <div className="flex w-4/6 flex-col items-start bg-slate-800 p-6">
          {details.map((item, index) => {
            return (
              <div className="flex flex-col items-start bg-slate-800 w-4/6 p-6">
                <h3 className="text-xl text-white">{item.title}</h3>
                <p>{item.description}</p>
                <input type="text" placeholder={item.title} className="" />
              </div>
            );
          })}
        </div>

      </div>
    </React.Fragment>
  );
}
