import React, { useEffect } from "react";
import stack_overflow_icon from "../stack_overflow_icon.png";
import { MdMenu } from "react-icons/md";
import { FaUserCircle } from "react-icons/fa";
import { FaSearch } from "react-icons/fa";
import AskQuestion from "./AskQuestionBar";
import Leftbar from "./Leftbar";
import Navbar from "./Navbar";
import Rightbar from "./Rightbar";
import InfiniteScroll from "react-infinite-scroll-component";
import { Link } from "react-router-dom";

export default function Home() {
  const sortingButtons = ["Bountied", "Hot", "Week", "Month"];
  const [questions, setQuestions] = React.useState<any[]>([]);

  useEffect(() => {
    var todos = Gun().get("todos"); 
    todos.map().once(function (todo, id) {
      console.log("<><>", todo, id);
      todo &&
        setQuestions((prevState: any) => [
          ...prevState,
          { title: todo["title"], id: id },
        ]);
    });
  }, []);

  function showQuestions(): any {
    return questions.map((question) => {
      if (question !== null) {
        return (
          <div className="flex flex-col items-start py-4">
            <div className="flex gap-4 md:flex-col text-right w-full content-start px-4">
              <span>{"2 vote"}</span>
              <span>{"2 answers"}</span>
              <span>{"2 views"}</span>
            </div>
            <div className="px-4 text-c">{question.title}</div>
          </div>
        );
      }
    });
  }

  return (
    <React.Fragment>
      <div className="h-screen w-full bg-neutral-800 sm:w-7/12">
        <div className="mx-4 flex items-center justify-between pt-4 sm:items-start">
          <p className="text-xl text-white md:text-3xl">Top Questions</p>
          <Link
            to={"/askquestion"}
            className="cursor-pointer rounded-sm bg-blue-500 py-2 px-2 text-center text-white sm:h-10"
          >
            Ask Question
          </Link>
        </div>

        <div className="relative mx-4 mt-6 flex justify-end">
          {sortingButtons.map((buttonName) => {
            return (
              <button className="border-collapse border border-gray-400 p-3 text-white transition-all duration-100 hover:bg-slate-600">
                {buttonName}
              </button>
            );
          })}
        </div>

        <div>{showQuestions()}</div>
        {/* <InfiniteScroll
          dataLength={items.length} //This is important field to render the next data
          next={() => }
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >
          {showQuestions()}
        </InfiniteScroll> */}
      </div>
      <Rightbar />
    </React.Fragment>
  );
}
