import React from "react";
import { Link } from "react-router-dom";

export default function AskQuestion() {
  const details = [
    {
      title: "Title",
      description:
        'Be specific and imagine you"re asking a question to another person.',
      inputWidth: "h-10 w-full p",
    },
    {
      title: "What are the details of your problem?",
      description:
        'Introduce the problem and expand on what you put in the title. Minimum 20 characters.',
      inputWidth: "h-40 w-full",
    },
    {
      title: "Title",
      description:
        'Be specific and imagine you"re asking a question to another person.',
      inputWidth: "h-20 w-full",
    },
  ];
  return (
    <React.Fragment>
      <div className="bg-stone-700 pl-5">
        <h1 className="py-5 text-left text-2xl font-bold text-white">
          Ask a public question
        </h1>


          {details.map((item, index) => {
            return (
              <div className="flex flex-col items-start bg-zinc-800 w-4/6 p-6 m-5">
                <h3 className="text-xl text-white">{item.title}</h3>
                <p className="text-white">{item.description}</p>
                <input type="text" placeholder={item.title} className={`${item.inputWidth} my-3 bg-zinc-700 text-black resize-none`}  />
              </div>
            );
          })}

      </div>
    </React.Fragment>
  );
}
